                    td3.innerHTML = row.prezime;
                     td4.innerHTML = row.datumUpisa;
                   td5.innerHTML = row.odjeljenje;
                    var b1 = document.createElement('button');

                    b1.innerText = ""Obrisi"";
                  b1.setAttribute(""onclick"", ""ObrisiUcenikSekcija("" + row.id + "" )"");
                 b1.classList.add('btn');
                    td6.appendChild(b1);
                    tr.appendChild(td1);
                    tr.appendChild(td2);
                    tr.appendChild(td3);
                    tr.appendChild(td4);
                    tr.appendChild(td5);
                    tr.appendChild(td6);
    };
